"use client";

import { Button } from "@/components/ui/button";
import { Form } from "@/components/ui/form";
import { useCallback } from "react";
import { useForm } from "react-hook-form";
import { z } from "zod";
import { authClient } from "@/lib/auth-client";
import { zodResolver } from "@hookform/resolvers/zod";
import { NameField } from "@/components/name-field";
import { PasswordField } from "@/components/password-field";
import { EmailField } from "@/components/email-field";
import { RootError } from "@/components/root-error";
import type { ErrorContext, SuccessContext } from "better-auth/react";
import { cn } from "@/lib/utils";
import { toast } from "sonner";

const formSchema = z.object({
  name: z.string().min(2, {
    message: "Name must be at least 2 characters",
  }),
  email: z.string().email(),
  password: z.string().min(6, {
    message: "Password must be at least 6 characters",
  }),
});

export type FormSchema = z.infer<typeof formSchema>;

export interface SignUpProps {
  onSuccess?: (context: SuccessContext) => void;
  onError?: (context: ErrorContext) => void;
  callbackURL?: string;
  className?: string;
}

export function SignUp(props?: SignUpProps) {
  const form = useForm({
    resolver: zodResolver(formSchema),
    defaultValues: {
      email: "",
      name: "",
      password: "",
    },
  });

  const onSubmit = useCallback(
    (values: FormSchema) => {
      authClient.signUp.email(
        {
          ...values,
          callbackURL: props?.callbackURL,
        },
        {
          onSuccess(context) {
            toast.success(`Welcome ${context.data.name}!`);
            return props?.onSuccess?.(context);
          },
          onError(context) {
            toast.error(`There was an issue signing you up.`, {
              description: <>{context.error.message}</>,
            });
            form.setError("root", { message: context.error.message });
            return props?.onError?.(context);
          },
        }
      );
    },
    [props, form.setError]
  );

  return (
    <div
      className={cn(
        "space-y-8 w-96 border border-border rounded-2xl p-8 shadow-2xl bg-card text-card-foreground",
        props?.className
      )}
    >
      <Form {...form}>
        <Title />
        <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-8">
          <NameField form={form} />
          <EmailField form={form} />
          <PasswordField form={form} />
          <RootError form={form} />
          <Button type="submit" className="w-full cursor-pointer">
            Create account
          </Button>
        </form>
        <AlreadyHaveAccount />
      </Form>
    </div>
  );
}

function Title() {
  return (
    <div className="w-full flex flex-col gap-2 justify-center items-center">
      <h1 className="text-lg font-bold">Create your account</h1>
      <p className="text-muted-foreground text-xs">
        Welcome! Please fill in your details to get started.
      </p>
    </div>
  );
}

function AlreadyHaveAccount() {
  return (
    <div className="w-full flex justify-center items-center gap-2 text-sm mt-5">
      <p className="text-muted-foreground">Already have an account?</p>
      <a href="/sign-in">Sign in</a>
    </div>
  );
}

import {
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import type { UseFormReturn } from "react-hook-form";

export function NameField({ form }: { form: UseFormReturn<any> }) {
  return (
    <FormField
      control={form.control}
      name="name"
      render={({ field }) => (
        <FormItem>
          <FormLabel>Name</FormLabel>
          <FormControl>
            <Input placeholder="Enter your name" {...field} />
          </FormControl>
          <FormMessage />
        </FormItem>
      )}
    />
  );
}

import {
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import type { UseFormReturn } from "react-hook-form";

export function PasswordField({ form }: { form: UseFormReturn<any> }) {
  return (
    <FormField
      control={form.control}
      name="password"
      render={({ field }) => (
        <FormItem>
          <FormLabel>Password</FormLabel>
          <FormControl>
            <Input
              type="password"
              placeholder="Enter your password"
              {...field}
            />
          </FormControl>
          <FormMessage />
        </FormItem>
      )}
    />
  );
}

import {
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import type { UseFormReturn } from "react-hook-form";

export function EmailField({
  form,
}: {
  form: UseFormReturn<any>;
}) {
  return (
    <FormField
      control={form.control}
      name="email"
      render={({ field }) => (
        <FormItem>
          <FormLabel>Email</FormLabel>
          <FormControl>
            <Input
              type="email"
              placeholder="Enter your email address"
              {...field}
            />
          </FormControl>
          <FormMessage />
        </FormItem>
      )}
    />
  );
}

import type { UseFormReturn } from "react-hook-form";

export function RootError({ form }: { form: UseFormReturn<any> }) {
  if (!form.formState.errors.root) return null;
  return (
    <p className="text-destructive-foreground text-sm mb-3">
      {form.formState.errors.root.message}
    </p>
  );
}
